# ===== HELPER FUNCTIONS =====

def sanitize_ollama_options(opts: Optional[Dict[str, Any]]) -> Dict[str, Any]:
    if not opts:
        return {}
    return {k: v for k, v in opts.items() if k in ALLOWED_OLLAMA_KEYS}

def ollama_summarize(
    prompt: str,
    options: Optional[Dict[str, Any]] = None,
    system: Optional[str] = None,
    timeout: int = 600,
    stream: bool = True,  # ‡πÄ‡∏õ‡∏¥‡∏î streaming ‡πÄ‡∏õ‡πá‡∏ô default
) -> str:
    base = OLLAMA_API
    if system is None:
        system = SYSTEM_PROMPT_TH
    
    payload = {
        "model": OLLAMA_MODEL,
        "stream": stream,
        "messages": [
            {"role": "system", "content": system},
            {"role": "user", "content": prompt},
        ],
    }

    try:
        if stream:
            # Streaming mode
            resp = requests.post(base, json=payload, timeout=timeout, stream=True)
            resp.raise_for_status()
            
            full_content = ""
            for line in resp.iter_lines(decode_unicode=True):
                if line:
                    try:
                        data = json.loads(line)
                        chunk = (data.get("message") or {}).get("content", "")
                        if chunk:
                            print(chunk, end="", flush=True)
                            full_content += chunk
                        # Check if done
                        if data.get("done"):
                            break
                    except json.JSONDecodeError:
                        pass
            print()  # newline at the end
            return full_content.strip()
        else:
            # Non-streaming mode
            resp = requests.post(base, json=payload, timeout=timeout)
            resp.raise_for_status()
            data = resp.json()
            msg = (data.get("message") or {}).get("content", "")
            return (msg or "").strip()
    except Exception as e:
        print(f"Ollama Error: {e}")
        return ""


def looks_thai(s: str) -> bool:
    return bool(THAI_RE.search(s or ""))

def ensure_thai(text: str, max_chars: int = None) -> str:
    t = (text or "").strip()
    if looks_thai(t):
        if max_chars and len(t) > max_chars:
            t = t[:max_chars]
        return t
    p1 = f"‡πÅ‡∏õ‡∏•‡πÄ‡∏õ‡πá‡∏ô‡∏†‡∏≤‡∏©‡∏≤‡πÑ‡∏ó‡∏¢: {t}"
    return (ollama_summarize(p1) or "").strip()

def word_count_th(text: str) -> int:
    t = (text or "").strip()
    if not t: return 0
    return len([w for w in WS_SPLIT_RE.split(t) if w])

